function  fid  =  gfopen(file_name, mode)%%  Graceful file open function.%%     Opens the file specified by  file_name  with the access%  permission given by  mode  and returns the pointer  fid  that%  is associated with the file. Writes an error message and returns%  if unable to open the file.%[fid, message]  =  fopen(file_name, mode);if fid == -1   fprintf('unable to open file %s \n', file_name);   return;end  